#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\PROJECT\@editor\node_modules\.pnpm\shelljs@0.8.4\node_modules\shelljs\bin\node_modules;D:\PROJECT\@editor\node_modules\.pnpm\shelljs@0.8.4\node_modules\shelljs\node_modules;D:\PROJECT\@editor\node_modules\.pnpm\shelljs@0.8.4\node_modules;D:\PROJECT\@editor\node_modules\.pnpm\node_modules;D:\PROJECT\@editor\node_modules;D:\PROJECT\node_modules;D:\node_modules;D:\PROJECT\@editor\packages\cmd\node_modules;D:\PROJECT\@editor\packages\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/PROJECT/@editor/node_modules/.pnpm/shelljs@0.8.4/node_modules/shelljs/bin/node_modules:/mnt/d/PROJECT/@editor/node_modules/.pnpm/shelljs@0.8.4/node_modules/shelljs/node_modules:/mnt/d/PROJECT/@editor/node_modules/.pnpm/shelljs@0.8.4/node_modules:/mnt/d/PROJECT/@editor/node_modules/.pnpm/node_modules:/mnt/d/PROJECT/@editor/node_modules:/mnt/d/PROJECT/node_modules:/mnt/d/node_modules:/mnt/d/PROJECT/@editor/packages/cmd/node_modules:/mnt/d/PROJECT/@editor/packages/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../../../../node_modules/.pnpm/shelljs@0.8.4/node_modules/shelljs/bin/shjs" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../../../../node_modules/.pnpm/shelljs@0.8.4/node_modules/shelljs/bin/shjs" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../../../../node_modules/.pnpm/shelljs@0.8.4/node_modules/shelljs/bin/shjs" $args
  } else {
    & "node$exe"  "$basedir/../../../../node_modules/.pnpm/shelljs@0.8.4/node_modules/shelljs/bin/shjs" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
